// Unit test example for conversation analysis
const { analyzeConversation } = require('../src/conversationAnalysis');

describe('Conversation Analysis', () => {
  test('should extract topics from conversation', async () => {
    const sampleConversation = 'I want to create a YouTube content planner with a dreamy aesthetic';
    const result = await analyzeConversation(sampleConversation);

    expect(result.topics).toContain('YouTube');
    expect(result.topics).toContain('content planning');
    expect(result.userPreferences.aestheticKeywords).toContain('dreamy');
  });

  test('should identify planning elements', async () => {
    const sampleConversation = 'I need a weekly schedule and a content board with status tags';
    const result = await analyzeConversation(sampleConversation);

    expect(result.planningElements).toContainEqual({
      type: 'schedule',
      frequency: 'weekly'
    });
    expect(result.planningElements).toContainEqual({
      type: 'board',
      properties: ['status']
    });
  });
});
```

